if( ! function_exists( 'elegant_shop_get_posts' ) ) :
	/**
	 * Fuction to list Custom Post Type
	*/
	function elegant_shop_get_posts( $post_type = 'post', $slug = false ){    
		$args = array(
			'posts_per_page'   => -1,
			'post_type'        => $post_type,
			'post_status'      => 'publish',
			'suppress_filters' => true 
		);
		$posts_array = get_posts( $args );
		
		// Initate an empty array
		$post_options = array();
		$post_options[''] = __( ' -- Choose -- ', 'elegant-shop' );
		if ( ! empty( $posts_array ) ) {
			foreach ( $posts_array as $posts ) {
				if( $slug ){
					$post_options[ $posts->post_title ] = $posts->post_title;
				}else{
					$post_options[ $posts->ID ] = $posts->post_title;    
				}
			}
		}
		return $post_options;
		wp_reset_postdata();
	}
endif;

if ( ! function_exists( 'elegant_shop_category' ) ) :
	/**
	 * Prints categories
	 */
	function elegant_shop_category(){
		// Hide category and tag text for pages.
		if ( 'post' === get_post_type()) {
			/* translators: used between list items, there is a space after the comma */
			$categories_list = get_the_category();
			echo '<span class="cat-links banner__stitle" itemprop="about">';
			foreach( $categories_list as $cat_list ){
				echo '<a href="' . esc_url( get_category_link( $cat_list->term_id ) ) . '" rel="category tag">' . $cat_list->name . '</a>';
			}
			echo '</span>';
		}
	}
endif;

